import React, { useState } from 'react';

const timelineData = [
  {
    year: '2018-2021',
    title: 'Professional Football',
    description: 'Description of the job and achievements.'
  },
  {
    year: '2021-2024',
    title: 'Publicis Media, London',
    description: 'Description of the job and achievements.'
  },
  {
    year: '2025',
    title: 'Entrepreneurship',
    description: 'Description of the job and achievements.'
  }
];

function Work() {
  const [openIndex, setOpenIndex] = useState(null);

  const handleToggle = idx => {
    setOpenIndex(openIndex === idx ? null : idx);
  };

  return (
    <div className="sleek-timeline-wrapper">
      <div className="sleek-timeline">
        {timelineData.map((item, idx) => (
          <div className={`sleek-timeline-item${idx === timelineData.length - 1 ? ' last' : ''}`} key={item.year}>
            <div className="sleek-timeline-dot" />
            <button className="sleek-timeline-title" onClick={() => handleToggle(idx)}>
              <span className="sleek-timeline-year">{item.year}</span>
              <span>{item.title}</span>
            </button>
            {openIndex === idx && (
              <div className="sleek-timeline-details">{item.description}</div>
            )}
            {idx !== timelineData.length - 1 && <div className="sleek-timeline-line" />}
            {idx === timelineData.length - 1 && <div className="sleek-timeline-arrow" />}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Work;
