import Chatbot from 'react-chatbot-kit';

const config = {
    botName: "MyBot",
    initialMessages: 'Hey, how can I help you today?',
    customStyles: {
      botChatBox: {
        backgroundColor: '#f0f0f0'
      }
    },
    customComponents: {
      botAvatar: (props) => (
        <img 
          src="./bitmoji.png" 
          alt="Custom Bot Avatar" 
          style={{ 
            width: '40px', 
            height: '40px', 
            borderRadius: '50%' 
          }} 
        />
      )
    }
  }




const MyChatbot = () => {
  return (
    <div>
      <Chatbot
        config={config}
        messageParser={MessageParser}
        actionProvider={ActionProvider}
      />
    </div>
  );
};

export default MyChatbot;
