import React from 'react';
import BlogCard from './BlogCard';

function Blog() {
  const blogPosts = [
    {
      title: "The Future of Startups: Verticalised Agents as First Hires",
      subtitle: "Speculating on the impact of A.I systems, specifically verticalised agents, as tech companies first hires.",
      link: "https://www.theaihire.com/blog/future-of-startups"
    }
  ];

  return (
    <div className="section" id="blog">
      <div className="blog-container">
        {blogPosts.map((post, index) => (
          <BlogCard key={index} {...post} />
        ))}
        {/* Add empty blog cards to match the size of the projects section */}
        <div className="blog-card"></div>
        <div className="blog-card"></div>
      </div>
    </div>
  );
}

export default Blog;
