import React, { useState, useEffect } from 'react';
import hi from '../assets/images/hi.png';

// RotatingText component with typing effect
function RotatingText() {
  const items = ['tech', 'startups', 'business', 'entrepreneurship', 'football', 'my friends and family'];
  const [index, setIndex] = useState(0);
  const [displayedText, setDisplayedText] = useState('');
  const [charIndex, setCharIndex] = useState(0);

  useEffect(() => {
    const typingInterval = setInterval(() => {
      setDisplayedText((prev) => prev + items[index][charIndex]);
      setCharIndex((prev) => prev + 1);

      if (charIndex === items[index].length) {
        clearInterval(typingInterval);
        setTimeout(() => {
          setDisplayedText('');
          setCharIndex(0);
          setIndex((prevIndex) => (prevIndex + 1) % items.length);
        }, 1000); // Pause before starting the next word
      }
    }, 100); // Typing speed

    return () => clearInterval(typingInterval);
  }, [charIndex, index, items]);

  return <span>{displayedText}</span>;
}

